package three;

import java.util.HashMap;
import java.util.Map;

/**
 * Created by martin on 17-10-1.
 * 相当于查询所有的,也就是 n n 然后对于子序列 使用 利用
 * 1. 题目含义理解错误, 应该是长度,但是返回的是字母的个数
 * 2. 时间不满足, 使用双层的结构进行处理
 * 3. 算法分析含问题的,不知道如何处理
 */
public class NintyFive {
    public static void main(String[] args) {
        NintyFive nf = new NintyFive();
        String s = "";
        System.out.println(nf.longestSubstring(s, 301));
    }
   public int longestSubstring(String s, int k) {
        // 其实是使用暴力,但是实际上需要继承前面的数据
        if(s.length() == 0 || k <= 0 || s.length() < k)
            return 0;

        int max_length = 0;
        for (int i = 0; i <= s.length() - k; i++) {
            // i 到 i + k - 1
            // 初始化整个列表
            HashMap<Character, Integer> ori = new HashMap<>();
            HashMap<Character, Integer> ok = new HashMap<>();
            HashMap<Character, Integer> notOk = new HashMap<>();

            for (int j = i; j <= i + k - 1; j++) {
                char c = s.charAt(j);
                if(ori.containsKey(c)){
                    ori.put(c, ori.get(c) + 1);
                }else{
                    ori.put(c, 1);
                }
            }
            // 开始的时候全部都是添加到 ok
            // 然后分离

            for(Map.Entry<Character, Integer> entry: ori.entrySet()){
                if(entry.getValue() < k){
                    notOk.put(entry.getKey(), entry.getValue());
                }else {
                    ok.put(entry.getKey(), entry.getValue());
                }
            }
            if(notOk.isEmpty()){
                int len = length(ok);
                if(max_length < len){
                    max_length = len;
                }
            }


            for (int j = i + k; j < s.length() ; j++) { // j 是新添加的内容
                char c = s.charAt(j);
                if(ok.containsKey(c)){
                    ok.put(c, ok.get(c) + 1); // ok 中间含有, 很好
                }else {
                    if(notOk.containsKey(c)){
                        notOk.put(c, notOk.get(c) + 1);
                    }else {
                        notOk.put(c, 1);
                    }
                    // 添加之后, 查看是不是到达要求

                    if(notOk.get(c) == k){
                        ok.put(c, k);
                        notOk.remove(c);
                    }
                }
                if(notOk.isEmpty()){
                    int len = length(ok);
                    if(max_length < len){
                        max_length = len;
                    }
                }

            }
        }
        return max_length;
    }

    private static int length(HashMap<Character, Integer> s){
        int sum = 0;
        for(Map.Entry<Character, Integer> entry:s.entrySet()){
           sum += entry.getValue();
        }
        return sum;
    }


}
